// Helper function to get questions for a specific control
function getCCAQuestions(controlId) {
    // Try direct lookup first
    if (CCA_INTERVIEW_QUESTIONS[controlId]) {
        return CCA_INTERVIEW_QUESTIONS[controlId];
    }
    
    // Try matching by the numeric part (e.g., "3.1.1" matches "AC.L2-3.1.1")
    for (const key of Object.keys(CCA_INTERVIEW_QUESTIONS)) {
        const numericPart = key.split('-')[1];
        if (numericPart && (controlId.includes(numericPart) || numericPart.includes(controlId))) {
            return CCA_INTERVIEW_QUESTIONS[key];
        }
    }
    
    return null;
}

// Get all questions for a domain
function getCCAQuestionsByDomain(domain) {
    return Object.entries(CCA_INTERVIEW_QUESTIONS)
        .filter(([_, data]) => data.domain.toLowerCase().includes(domain.toLowerCase()))
        .map(([id, data]) => ({ controlId: id, ...data }));
}

// Get questions by category across all controls
function getCCAQuestionsByCategory(category) {
    const results = [];
    for (const [controlId, data] of Object.entries(CCA_INTERVIEW_QUESTIONS)) {
        const matching = data.interviewQuestions.filter(q => 
            q.category.toLowerCase().includes(category.toLowerCase())
        );
        if (matching.length > 0) {
            results.push({ controlId, domain: data.domain, requirement: data.requirement, questions: matching });
        }
    }
    return results;
}

// Export for use in app
if (typeof window !== 'undefined') {
    window.CCA_INTERVIEW_QUESTIONS = CCA_INTERVIEW_QUESTIONS;
    window.getCCAQuestions = getCCAQuestions;
    window.getCCAQuestionsByDomain = getCCAQuestionsByDomain;
    window.getCCAQuestionsByCategory = getCCAQuestionsByCategory;
}
